create database if not exists user_center;

-- 用户信息表
create table if not exists user_center.`userInfo`
(
    `id` bigint not null auto_increment comment 'id' primary key,
    `userName` varchar(256) not null comment '用户名',
    `userAccount` varchar(256) not null comment '账号',
    `userAvatarUrl` varchar(256) not null comment '头像',
    `gender` tinyint not null comment '性别',
    `userPassword` varchar(256) not null comment '用户密码',
    `phone` varchar(256) not null comment '电话',
    `email` varchar(256) not null comment '邮箱',
    `userStatus` varchar(256) not null comment '用户状态',
    `createTime` datetime not null comment '创建时间',
    `updateTime` datetime not null comment '更新时间 ',
    `isDelete` tinyint default 0 not null comment '是否删除',
    `userRole` tinyint not null comment '用户等级'
) comment '用户信息表';

